import React, { Component } from "react";
import "./App.css";
import images from "./images.json";
import ImageCards from "./ImageCards";

class App extends Component {
  state = {
    images
  };


  arrangeImageRandom = id => {
    let images = this.state.images.filter(images => images.clicked !== false);
    this.setState({ images });
    alert("Button was clicked for this little buddy!");
    // console.log(images);
  };
  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm">Clicky Game!</div>
          <div className="col-sm">
            <p> Click on an Image to start playing!</p>
          </div>
          <div className="col-sm">
            <p>
              {" "}
              Your Score: <span>{currentScore} </span> Top Score:
              <span>{5 + 11} </span>
            </p>
          </div>
        </div>
        <div
          className="jumbotron jumbotron-fluid text-center newBackground"
          styleName="back"
        >
          <h1> Memory Game</h1>{" "}
          <p>
            Click on an image to earn points, but don't click on any more than
            once!
          </p>
        </div>
        <ImageCards id={images[1].id} image={images[1].image} arrangeImageRandom={this.arrangeImageRandom} />

        {/* {this.state.images.map(images => (
          <imageCard
            arrangeImageRandom={this.arrangeImageRandom}
            id={images.id}
            key={images.id}
            image={images.image}
          />
        ))} */}
      </div>
    );
  }
}

export default App;
