  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col-sm">Clicky Game!</div>
          <div className="col-sm">
            <p> Click on an Image to start playing!</p>
          </div>
          <div className="col-sm">
            <p>
              {" "}
              Your Score: <span>{2 + 2} </span> Top Score:
              <span>{5 + 11} </span>
            </p>
          </div>
        </div>
        <div
          className="jumbotron jumbotron-fluid text-center newBackground"
          styleName="back"
        >
          <h1> Memory Game</h1>{" "}
          <p>
            Click on an image to earn points, but don't click on any more than
            once!
          </p>
        </div>
        {this.state.images.map(image => (
          <imageCard
          arrangeImageRandom={this.arrangeImageRandom}
            id={images.id}
            key={images.id}
            image={images.image}
          />
        ))}
      </div>
    );
  }
}

export default App;
